import React from 'react';
import { useSelector, useDispatch } from "react-redux";
import { incNumber, decNumber } from "./action/index";


const App = () => {

    const myState = useSelector((state) => state.changeTheNumber);
    const dispatch = useDispatch();

    return (
      <div className="container">
        <h1>Simple Calculator</h1>
        <div className="quantity">
          <a className="quantity_minus" tittle="Decrement"><span>-</span></a>
          <input className="quantity" type="number" className="qunatity_input" value="10" />
          <a className="quantity_plus" tittle="Increment" onClick={ () => dispatch(incNumber()) } ><span>+</span></a>
        </div>
      </div>
    )
  }


export default App;
